import React, { useState } from 'react';
import { 
  Thermometer,
  CloudRain,
  Wind,
  Fire,
  Droplets,
  Sun,
  CloudSnow,
  Eye
} from 'lucide-react';

const MonitoringPanel = ({ onLayerChange, selectedLayer, coordinates }) => {
  const [activeCategory, setActiveCategory] = useState('weather');

  const layerCategories = {
    weather: {
      title: 'Weather Layers',
      icon: CloudRain,
      layers: [
        { id: 'MODIS_Terra_Cloud_Effective_Radius', name: 'Cloud Coverage', icon: CloudRain },
        { id: 'IMERG_Precipitation_Rate', name: 'Precipitation', icon: CloudRain },
        { id: 'MODIS_Terra_Land_Surface_Temp_Day', name: 'Temperature', icon: Thermometer },
        { id: 'MERRA2_Wind_Speed_10m', name: 'Wind Speed', icon: Wind },
      ]
    },
    monitoring: {
      title: 'Environmental Monitoring',
      icon: Eye,
      layers: [
        { id: 'MODIS_Terra_Thermal_Anomalies_Day', name: 'Active Fires', icon: Fire },
        { id: 'MODIS_Terra_NDSI_Snow_Cover', name: 'Snow Cover', icon: CloudSnow },
        { id: 'MODIS_Terra_Water_Vapor', name: 'Water Vapor', icon: Droplets },
        { id: 'MODIS_Terra_Aerosol', name: 'Aerosol', icon: Sun },
      ]
    }
  };

  return (
    <div className="bg-white rounded-lg shadow p-4">
      <h2 className="text-xl font-semibold mb-4">Environmental Monitoring</h2>
      
      {/* Location Info */}
      {coordinates && coordinates.lat && coordinates.lon && (
        <div className="mb-4 p-3 bg-primary-50 rounded-lg text-sm">
          <p className="text-primary-700">
            Monitoring Location: {coordinates.lat.toFixed(4)}°, {coordinates.lon.toFixed(4)}°
          </p>
        </div>
      )}
      
      {/* Category Tabs */}
      <div className="flex space-x-2 mb-4">
        {Object.entries(layerCategories).map(([category, { title, icon: Icon }]) => (
          <button
            key={category}
            onClick={() => setActiveCategory(category)}
            className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-colors ${
              activeCategory === category 
                ? 'bg-primary-100 text-primary-700' 
                : 'text-gray-600 hover:bg-gray-100'
            }`}
          >
            <Icon className="h-4 w-4" />
            <span>{title}</span>
          </button>
        ))}
      </div>
      
      {/* Layer Selection */}
      <div className="space-y-2">
        {layerCategories[activeCategory].layers.map((layer) => {
          const Icon = layer.icon;
          const isSelected = selectedLayer === layer.id;
          
          return (
            <button
              key={layer.id}
              onClick={() => onLayerChange(layer.id)}
              className={`w-full flex items-center space-x-3 p-3 rounded-lg transition-colors ${
                isSelected
                  ? 'bg-primary-100 text-primary-700 ring-2 ring-primary-500 ring-opacity-50'
                  : 'hover:bg-gray-50'
              }`}
            >
              <Icon className={`h-5 w-5 ${isSelected ? 'text-primary-600' : 'text-gray-500'}`} />
              <div className="flex-1 text-left">
                <p className={`font-medium ${isSelected ? 'text-primary-700' : 'text-gray-700'}`}>
                  {layer.name}
                </p>
              </div>
            </button>
          );
        })}
      </div>
    </div>
  );
};

export default MonitoringPanel;
