import BaseApiService from './baseApiService';
import axios from 'axios';

class GeocodeService extends BaseApiService {
  constructor() {
    super();
    // Use environment variable or fallback to direct URL
    this.nominatimBase = process.env.REACT_APP_GEOCODE_API_URL || 'https://nominatim.openstreetmap.org';
    this.defaultParams = {
      format: 'json',
      addressdetails: 1,
      limit: 8,
      'accept-language': 'en'
    };
    
    // Create throttled methods to prevent rate limiting
    this.throttledSearch = this.throttle(this.search.bind(this), 1000);
    this.throttledReverse = this.throttle(this.reverse.bind(this), 1000);
  }

  // Throttle helper to prevent too many requests
  throttle(func, limit) {
    let inThrottle;
    return function(...args) {
      if (!inThrottle) {
        func.apply(this, args);
        inThrottle = true;
        setTimeout(() => inThrottle = false, limit);
      }
    }
  }

  /**
   * Search for locations using Nominatim (OpenStreetMap)
   * @param {string} q - search query
   * @returns {Promise<Array>} array of results { name, lat, lon, type, country }
   */
  async search(q) {
    if (!q || !q.trim()) return [];
    const url = `${this.nominatimBase}/search`;
    
    try {
      const params = { 
        q, 
        ...this.defaultParams,
        // Add timestamp to prevent caching
        _t: Date.now()
      };
      
      const res = await axios.get(url, { 
        params,
        timeout: 8000,
        headers: {
          'Accept': 'application/json',
          'Cache-Control': 'no-cache'
        }
      });

      // Process and validate the response
      if (!Array.isArray(res.data)) {
        console.warn('Unexpected response format:', res.data);
        return [];
      }

      return res.data.map(item => ({
        name: item.display_name.split(',')[0] || item.display_name,
        fullName: item.display_name,
        lat: parseFloat(item.lat),
        lon: parseFloat(item.lon),
        type: item.type || 'place',
        country: item.address?.country || '',
        city: item.address?.city || item.address?.town || item.address?.village || '',
        state: item.address?.state || '',
        timestamp: Date.now()
      }));
    } catch (err) {
      console.warn('Geocode search failed:', err.message);
      if (err.response?.status === 429) {
        // Handle rate limiting
        console.warn('Rate limit reached, retrying in 1 second...');
        await new Promise(resolve => setTimeout(resolve, 1000));
        return this.search(q);
      }
      return [];
    }
  }

  /**
   * Reverse geocode lat/lon to a human-friendly name
   */
  async reverse(lat, lon) {
    if (!this.isValidCoordinates(lat, lon)) return null;
    const url = `${this.nominatimBase}/reverse`;
    
    try {
      const params = { 
        lat, 
        lon, 
        format: 'json', 
        addressdetails: 1, 
        'accept-language': 'en',
        // Add timestamp to prevent caching
        _t: Date.now()
      };
      
      const res = await axios.get(url, { 
        params,
        timeout: 8000,
        headers: {
          'Accept': 'application/json',
          'Cache-Control': 'no-cache'
        }
      });
      
      return {
        name: res.data.display_name,
        lat: parseFloat(res.data.lat),
        lon: parseFloat(res.data.lon),
        address: res.data.address
      };
    } catch (err) {
      console.warn('Reverse geocode failed:', err.message);
      return null;
    }
  }
}

export default new GeocodeService();
