/**
 * GIBSLayerGrid
 * Fetches WMTS GetCapabilities from GIBSService, parses XML, and displays a responsive grid
 */
function GIBSLayerGrid() {
  const [layers, setLayers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const CACHE_KEY = 'gibs_capabilities_cache_v1';
  const CACHE_TTL_MS = 1000 * 60 * 60 * 24; // 24 hours

  useEffect(() => {
    let mounted = true;

    async function load() {
      try {
        setLoading(true);

        // Try cache first
        const raw = localStorage.getItem(CACHE_KEY);
        if (raw) {
          try {
            const parsedCache = JSON.parse(raw);
            if (parsedCache.timestamp && (Date.now() - parsedCache.timestamp) < CACHE_TTL_MS) {
              if (mounted) {
                setLayers(parsedCache.layers.slice(0, 20));
                setLoading(false);
              }
              return;
            }
          } catch (err) {
            console.warn('Invalid GIBS cache, refetching');
          }
        }

        const xmlText = await GIBSService.getCapabilities();

        // Parse XML using DOMParser (browser environment)
        const parser = new DOMParser();
        const doc = parser.parseFromString(xmlText, 'application/xml');

        const layerNodes = Array.from(doc.querySelectorAll('Layer'));
        const parsed = layerNodes.map(node => {
          const idNode = node.querySelector('Identifier');
          const titleNode = node.querySelector('Title');
          const tileMatrixSetNode = node.querySelector('TileMatrixSetLink > TileMatrixSet');
          return {
            id: idNode ? idNode.textContent.trim() : 'unknown',
            title: titleNode ? titleNode.textContent.trim() : idNode ? idNode.textContent.trim() : 'Untitled',
            tileMatrixSet: tileMatrixSetNode ? tileMatrixSetNode.textContent.trim() : null
          };
        }).filter(Boolean);

        // De-duplicate by id and prefer named title
        const unique = [];
        const seen = new Set();
        for (const p of parsed) {
          if (!seen.has(p.id)) {
            seen.add(p.id);
            unique.push(p);
          }
        }

        // Persist to cache
        try {
          localStorage.setItem(CACHE_KEY, JSON.stringify({ timestamp: Date.now(), layers: unique }));
        } catch (err) {
          console.warn('Failed to write GIBS cache', err.message);
        }

        // Limit to 20 items for the landing page
        if (mounted) setLayers(unique.slice(0, 20));
      } catch (e) {
        console.error('GIBS capabilities parse error', e);
        if (mounted) setError(e.message || String(e));
      } finally {
        if (mounted) setLoading(false);
      }
    }

    load();

    return () => { mounted = false; };
  }, []);

  if (loading) return <div className="text-center py-12">Loading GIBS layers…</div>;
  if (error) return <div className="text-center py-12 text-red-600">Failed to load GIBS layers: {error}</div>;

  const handleRefresh = () => {
    try { localStorage.removeItem(CACHE_KEY); } catch (e) { /* ignore */ }
    setLayers([]);
    setLoading(true);
    setError(null);
    // re-run effect by toggling a simple reload via setTimeout
    setTimeout(() => {
      // naive re-fetch: call load by re-mounting effect through state trick
      window.location.reload();
    }, 100);
  };

  return (
    <div>
      <div className="flex justify-end mb-4">
        <button onClick={handleRefresh} className="btn-secondary">Refresh Layers</button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-4">
        {layers.map((layer) => {
          // Build a small preview tile using GIBSService (low zoom)
          const previewUrl = GIBSService.getTileUrl({ layer: layer.id, tileMatrix: 1, tileRow: 0, tileCol: 0 });
          return (
            <div key={layer.id} className="card p-2 bg-white/70 hover:shadow-lg transition-shadow duration-200">
              <div className="h-32 bg-gray-100 rounded overflow-hidden mb-3 flex items-center justify-center">
                <img src={previewUrl} alt={layer.title} className="object-cover w-full h-full" loading="lazy" onError={(e)=>{e.currentTarget.style.display='none';}} />
              </div>
              <h4 className="font-semibold text-gray-900 truncate">{layer.title}</h4>
              <p className="text-sm text-gray-600 truncate">{layer.id}</p>
              <p className="text-xs text-gray-500 mt-2">TileMatrixSet: {layer.tileMatrixSet || 'n/a'}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
}

/**
 * WeatherGridWrapper
 * Fetches simulated weather analysis from weatherService for a set of sample locations,
 * maps the response to WeatherGrid's expected shape and provides a refresh button.
 */
function WeatherGridWrapper() {
  const [data, setData] = React.useState([]);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  // Sample locations (lat, lon, display name)
  // default sample locations (used if user doesn't enter a location)
  const LOCATIONS = [
    { id: 'nyc', name: 'New York, NY', lat: 40.7128, lon: -74.0060 },
    { id: 'la', name: 'Los Angeles, CA', lat: 34.0522, lon: -118.2437 },
    { id: 'chi', name: 'Chicago, IL', lat: 41.8781, lon: -87.6298 },
    { id: 'mia', name: 'Miami, FL', lat: 25.7617, lon: -80.1918 },
    { id: 'den', name: 'Denver, CO', lat: 39.7392, lon: -104.9903 }
  ];

  const fetchAll = React.useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      // Check landing page input for user-specified coordinates or query
      const input = (document.getElementById('landing-location-input') || {}).value || '';

      let targets = LOCATIONS;
      if (input && input.trim()) {
        // If input looks like lat,lon parse it
        const coordMatch = input.split(',').map(s => s.trim()).map(s => Number(s));
        if (coordMatch.length === 2 && !isNaN(coordMatch[0]) && !isNaN(coordMatch[1])) {
          targets = [{ id: 'custom', name: input, lat: coordMatch[0], lon: coordMatch[1] }];
        } else {
          // Otherwise try geocode lookup
          try {
            const geocoded = await GeocodeService.search(input);
            if (geocoded && geocoded.length > 0) {
              targets = geocoded.slice(0, 5).map((g, i) => ({ id: `g_${i}`, name: g.name || `${g.lat},${g.lon}`, lat: g.lat, lon: g.lon }));
            }
          } catch (gErr) {
            console.warn('Geocode lookup failed', gErr);
          }
        }
      }

      // Fetch analysis for each target location in parallel
      const promises = targets.map(loc =>
        weatherService.getWeatherAnalysis(loc.lat, loc.lon, new Date().toISOString())
          .then(result => ({ loc, result }))
          .catch(err => ({ loc, error: err }))
      );

      const results = await Promise.all(promises);

      // Map each response into WeatherGrid shape using probabilities calculated from historical data
      const mapped = results.map(item => {
        if (item.error) {
          return {
            id: item.loc.id,
            location: item.loc.name,
            temp: null,
            feels_like: null,
            condition: 'clouds',
            wind_kmh: null,
            precip_mm: null,
            time: new Date().toISOString(),
            probability: null
          };
        }

        const hist = item.result || {};
        const latest = hist.daily ? hist.daily.slice(-1)[0] : {};

        // Use weatherCalculations to get probabilities
        let probabilities = { hot: 0, cold: 0, wet: 0, windy: 0, uncomfortable: 0 };
        try {
          probabilities = weatherCalculations.calculateProbabilities(hist, new Date().toISOString());
        } catch (calcErr) {
          console.warn('Probability calc failed', calcErr);
        }

        return {
          id: item.loc.id,
          location: item.loc.name,
          temp: latest.temperature != null ? latest.temperature : null,
          feels_like: latest.temperature != null ? latest.temperature : null,
          condition: guessConditionFromRecord(latest),
          wind_kmh: latest.windSpeed ?? null,
          precip_mm: latest.precipitation ?? null,
          time: item.result?.metadata?.generatedAt || new Date().toISOString(),
          probability: probabilities.wet // choose 'wet' as primary probability to display; adjust as needed
        };
      });

      setData(mapped);
    } catch (err) {
      console.error('WeatherGridWrapper fetch error', err);
      setError(err.message || String(err));
    } finally {
      setLoading(false);
    }
  }, []);

  React.useEffect(() => {
    fetchAll();

    // wire refresh and search button hooks (simple DOM hooks for landing page)
    const btn = document.getElementById('refresh-weather');
    const searchBtn = document.getElementById('landing-location-search');
    const input = document.getElementById('landing-location-input');

    const refreshHandler = () => fetchAll();
    const searchHandler = () => fetchAll();

    if (btn) btn.addEventListener('click', refreshHandler);
    if (searchBtn) searchBtn.addEventListener('click', searchHandler);
    if (input) {
      const keyHandler = (e) => { if (e.key === 'Enter') fetchAll(); };
      input.addEventListener('keypress', keyHandler);
      // cleanup for input
      return () => {
        input.removeEventListener('keypress', keyHandler);
        if (btn) btn.removeEventListener('click', refreshHandler);
        if (searchBtn) searchBtn.removeEventListener('click', searchHandler);
      };
    }

    return () => {
      if (btn) btn.removeEventListener('click', refreshHandler);
      if (searchBtn) searchBtn.removeEventListener('click', searchHandler);
    };
  }, [fetchAll]);

  return <WeatherGrid data={data} loading={loading} error={error} unit="C" />;
}

function guessConditionFromRecord(rec = {}) {
  // Simple heuristic: precipitation > 5 => thunder/rain; >0 => rain; low temp and precip => snow; else clear/clouds
  const p = rec.precipitation ?? 0;
  const t = rec.temperature != null ? rec.temperature : null;

  if (p >= 10) return 'thunder';
  if (p > 0) return 'rain';
  if (t !== null && t <= 0) return 'snow';
  // fallback by random small chance of clouds
  return Math.random() > 0.3 ? 'clear' : 'clouds';
}

function estimatePrecipProbability(rec = {}) {
  if (!rec || rec.precipitation == null) return null;
  // heuristic: map precipitation amount to a 0-100 probability
  const p = Math.min(100, Math.round((rec.precipitation / 20) * 100));
  return p;
}

/**
 * CMEAnalysisPanel
 * Calls weatherService.getCMEAnalysis with user-provided/default params and displays a summary.
 */
function CMEAnalysisPanel() {
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);
  const [result, setResult] = React.useState({ count: 0, items: [] });

  const defaultOpts = {
    startDate: '2016-09-01',
    endDate: '2016-09-30',
    mostAccurateOnly: true,
    speed: 500,
    halfAngle: 30,
    catalog: 'ALL'
  };

  const fetchCME = React.useCallback(async (opts = defaultOpts) => {
    setLoading(true);
    setError(null);
    try {
      const res = await weatherService.getCMEAnalysis(opts);
      setResult(res || { count: 0, items: [] });
    } catch (err) {
      console.error('CME fetch error', err);
      setError(err.message || String(err));
    } finally {
      setLoading(false);
    }
  }, []);

  React.useEffect(() => { fetchCME(); }, [fetchCME]);

  if (loading) return <div className="text-center py-6">Loading CME analysis…</div>;
  if (error) return <div className="text-center py-6 text-red-600">Failed to load CME Analysis: {error}</div>;

  return (
    <div>
      <div className="mb-4 flex items-center justify-between">
        <div className="text-sm text-gray-600">Found {result.count} CMEAnalysis items</div>
        <div>
          <button onClick={() => fetchCME()} className="btn-secondary">Refresh CME</button>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {result.items && result.items.length > 0 ? result.items.map((it, idx) => (
          <div key={it.activityID || idx} className="card p-4 bg-white/80">
            <h4 className="font-semibold text-gray-900">{it.activityID || 'CME'}</h4>
            <p className="text-xs text-gray-500">Catalog: {it.catalog}</p>
            <p className="text-sm text-gray-600 mt-2">Speed: {it.speed ?? 'n/a'}</p>
            <p className="text-sm text-gray-600">Half Angle: {it.halfAngle ?? 'n/a'}</p>
            <p className="text-xs text-gray-500 mt-2">Comment: {it.comment || '—'}</p>
          </div>
        )) : (
          <div className="text-gray-500">No CMEAnalysis items match the filters.</div>
        )}
      </div>
    </div>
  );
