import React, { useState, useEffect, useCallback } from 'react';
import GIBSService from '../services/api/gibsService';

const MonitoringPanel = ({ coordinates }) => {
  const [selectedLayer, setSelectedLayer] = useState('clouds');
  const [cloudCoverage, setCloudCoverage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const gibsService = new GIBSService();

  // Function to determine if it's daytime at the given coordinates
  const isDaytime = useCallback(() => {
    if (!coordinates?.lon) return true;
    
    const date = new Date();
    const hours = date.getUTCHours() + (coordinates.lon / 15); // Approximate local time
    return hours >= 6 && hours < 18; // Consider 6 AM to 6 PM as daytime
  }, [coordinates]);

  // Update layer based on time of day
  useEffect(() => {
    const newLayer = isDaytime() ? 'clouds' : 'cloudNight';
    setSelectedLayer(newLayer);
  }, [isDaytime]);

  // Fetch cloud coverage data
  useEffect(() => {
    const fetchCloudData = async () => {
      if (!coordinates) return;

      setLoading(true);
      setError(null);

      try {
        const layerInfo = gibsService.getLayerInfo(selectedLayer);
        if (!layerInfo) {
          throw new Error('Cloud layer information not available');
        }

        const isAvailable = await gibsService.isLayerAvailable(selectedLayer);
        if (!isAvailable) {
          throw new Error('Cloud data not available for the selected time');
        }

        // Set the cloud coverage data
        setCloudCoverage({
          layer: selectedLayer,
          title: layerInfo.title,
          description: layerInfo.description,
          time: new Date().toISOString()
        });
      } catch (err) {
        console.error('Error fetching cloud data:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchCloudData();
  }, [coordinates, selectedLayer]);

  if (!coordinates) {
    return (
      <div className="bg-white rounded-lg shadow-lg p-4">
        <p className="text-gray-500">Select a location to view weather monitoring data</p>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow-lg p-4">
      <h2 className="text-lg font-semibold mb-4">Weather Monitoring</h2>
      
      <div className="mb-4">
        <h3 className="text-md font-medium mb-2">Selected Location</h3>
        <p className="text-sm text-gray-600">
          Latitude: {coordinates?.lat?.toFixed(4) ?? 'N/A'}°<br />
          Longitude: {coordinates?.lon?.toFixed(4) ?? 'N/A'}°
        </p>
      </div>

      {loading && (
        <div className="text-center py-4">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500 mx-auto"></div>
          <p className="text-sm text-gray-500 mt-2">Loading weather data...</p>
        </div>
      )}

      {error && (
        <div className="bg-red-50 border border-red-200 rounded p-3 mb-4">
          <p className="text-sm text-red-600">{error}</p>
        </div>
      )}

      {cloudCoverage && !loading && !error && (
        <div>
          <div className="mb-4">
            <h3 className="text-md font-medium mb-2">{cloudCoverage.title}</h3>
            <p className="text-sm text-gray-600">{cloudCoverage.description}</p>
          </div>

          <div className="text-sm text-gray-500">
            <p>Last updated: {new Date(cloudCoverage.time).toLocaleString()}</p>
          </div>
        </div>
      )}

      <div className="mt-4 pt-4 border-t">
        <button
          className="w-full px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 disabled:opacity-50"
          onClick={() => setSelectedLayer(isDaytime() ? 'clouds' : 'cloudNight')}
          disabled={loading}
        >
          Refresh Data
        </button>
      </div>
    </div>
  );
};

export default MonitoringPanel;
