import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Cloud, Zap, Globe, TrendingUp, ArrowRight, Satellite } from 'lucide-react';
import { GIBSService } from '../services';
import WeatherGrid from './WeatherGrid';

const LandingPage = () => {
  const navigate = useNavigate();

  const handleGetStarted = () => {
    navigate('/dashboard');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      {/* Header */}
      <header className="relative z-10 px-6 py-4">
        <nav className="max-w-7xl mx-auto flex justify-between items-center">
          <div className="flex items-center space-x-2">
            <Satellite className="h-8 w-8 text-primary-600" />
            <span className="text-xl font-bold text-gray-900">WeatherPredict</span>
          </div>
          <div className="flex items-center space-x-4">
            <button 
              onClick={() => navigate('/storm-tracker')}
              className="btn-secondary"
            >
              Storm Tracker
            </button>
            <button 
              onClick={handleGetStarted}
              className="btn-primary"
            >
              Dashboard
            </button>
          </div>
        </nav>
      </header>

      {/* Hero Section */}
      <section className="relative px-6 py-20">
        <div className="max-w-7xl mx-auto">
          <div className="text-center">
            {/* Floating Animation Elements */}
            <div className="absolute top-10 left-10 animate-float">
              <Cloud className="h-16 w-16 text-blue-300 opacity-60" />
            </div>
            <div className="absolute top-20 right-20 animate-float" style={{animationDelay: '2s'}}>
              <Zap className="h-12 w-12 text-yellow-400 opacity-60" />
            </div>
            <div className="absolute bottom-20 left-20 animate-float" style={{animationDelay: '4s'}}>
              <Globe className="h-14 w-14 text-green-400 opacity-60" />
            </div>

            {/* Main Content */}
            <div className="relative z-10">
              <h1 className="text-5xl md:text-7xl font-bold text-gray-900 mb-6">
                Predict Your
                <span className="block text-transparent bg-clip-text bg-gradient-to-r from-primary-600 to-purple-600">
                  Weather Probability
                </span>
              </h1>

              <p className="text-xl md:text-2xl text-gray-600 mb-4 max-w-3xl mx-auto">
                NASA-Recommended Meteomatics Integration
              </p>

              <p className="text-lg text-gray-500 mb-12 max-w-2xl mx-auto">
                Following NASA's guidance, we integrate Meteomatics weather API with NASA DONKI space weather and EONET disaster tracking. 
                Discover weather probabilities and monitor real-time storms, hurricanes, and natural disasters worldwide.
              </p>

              <button 
                onClick={handleGetStarted}
                className="btn-primary text-lg inline-flex items-center space-x-2 group"
              >
                <span>Get Started</span>
                <ArrowRight className="h-5 w-5 group-hover:translate-x-1 transition-transform duration-200" />
              </button>
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="px-6 py-20 bg-white/50 backdrop-blur-sm">
        <div className="max-w-7xl mx-auto">
          <div className="text-center mb-16">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
              What Makes Us Different
            </h2>
            <p className="text-lg text-gray-600 max-w-2xl mx-auto">
              Following NASA's recommendation, we integrate Meteomatics weather API with NASA DONKI space weather data for comprehensive analysis
            </p>
          </div>

          <div className="grid md:grid-cols-3 gap-8">
            <div className="card text-center group hover:shadow-2xl transition-shadow duration-300">
              <div className="w-16 h-16 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4 group-hover:bg-primary-200 transition-colors duration-300">
                <Satellite className="h-8 w-8 text-primary-600" />
              </div>
              <h3 className="text-xl font-semibold text-gray-900 mb-3">NASA-Recommended API</h3>
              <p className="text-gray-600">
                Meteomatics professional weather data as recommended by NASA for research applications
              </p>
            </div>

            <div className="card text-center group hover:shadow-2xl transition-shadow duration-300">
              <div className="w-16 h-16 bg-secondary-100 rounded-full flex items-center justify-center mx-auto mb-4 group-hover:bg-secondary-200 transition-colors duration-300">
                <Cloud className="h-8 w-8 text-secondary-600" />
              </div>
              <h3 className="text-xl font-semibold text-gray-900 mb-3">AI-Powered Analysis</h3>
              <p className="text-gray-600">
                Machine learning algorithms analyze 15+ years of climate patterns for accurate predictions
              </p>
            </div>

            <div className="card text-center group hover:shadow-2xl transition-shadow duration-300">
              <div className="w-16 h-16 bg-purple-100 rounded-full flex items-center justify-center mx-auto mb-4 group-hover:bg-purple-200 transition-colors duration-300">
                <TrendingUp className="h-8 w-8 text-purple-600" />
              </div>
              <h3 className="text-xl font-semibold text-gray-900 mb-3">Space Weather Integration</h3>
              <p className="text-gray-600">
                NASA DONKI space weather data integration for comprehensive atmospheric analysis
              </p>
            </div>
          </div>
        </div>
      </section>

      {/* GIBS Layers Grid (fetched from NASA GIBS GetCapabilities) */}
      <section className="px-6 py-20">
        <div className="max-w-7xl mx-auto">
          <div className="text-center mb-8">
            <h2 className="text-2xl md:text-3xl font-bold text-gray-900 mb-2">Available Satellite / Imagery Layers (GIBS)</h2>
            <p className="text-gray-600 max-w-2xl mx-auto">Live list from NASA GIBS WMTS GetCapabilities (EPSG:4326). Showing a curated subset for monitoring.</p>
          </div>

          <GIBSLayerGrid />
        </div>
      </section>

      {/* Example Weather Grid - replace with real data from your weather service */}
      <section className="px-6 py-12 bg-white/60">
        <div className="max-w-7xl mx-auto">
          <div className="text-center mb-8">
            <h2 className="text-2xl md:text-3xl font-bold text-gray-900 mb-2">Sample Weather Overview</h2>
            <p className="text-gray-600 max-w-2xl mx-auto">A responsive grid that shows current weather snapshots for multiple locations.</p>
          </div>

          <WeatherGrid
            loading={false}
            data={[
              { id: 'nyc', location: 'New York, NY', temp: 22.3, feels_like: 21, condition: 'clouds', wind_kmh: 18, precip_mm: 0.2, time: new Date().toISOString(), probability: 12 },
              { id: 'la', location: 'Los Angeles, CA', temp: 26.1, feels_like: 26, condition: 'clear', wind_kmh: 8, precip_mm: 0, time: new Date().toISOString(), probability: 0 },
              { id: 'chi', location: 'Chicago, IL', temp: 18.6, feels_like: 17, condition: 'rain', wind_kmh: 22, precip_mm: 3.4, time: new Date().toISOString(), probability: 78 },
              { id: 'mia', location: 'Miami, FL', temp: 30.2, feels_like: 33, condition: 'thunder', wind_kmh: 14, precip_mm: 6.7, time: new Date().toISOString(), probability: 66 },
              { id: 'den', location: 'Denver, CO', temp: 15.4, feels_like: 14, condition: 'snow', wind_kmh: 10, precip_mm: 0, time: new Date().toISOString(), probability: 5 }
            ]}
          />
        </div>
      </section>

      {/* How It Works Section */}
      <section className="px-6 py-20">
        <div className="max-w-7xl mx-auto">
          <div className="text-center mb-16">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
              How It Works
            </h2>
            <p className="text-lg text-gray-600 max-w-2xl mx-auto">
              Simple steps to get your weather probability predictions
            </p>
          </div>

          <div className="grid md:grid-cols-4 gap-8">
            {[
              { step: '01', title: 'Choose Location', desc: 'Search by city or click on the map' },
              { step: '02', title: 'Select Date', desc: 'Pick a specific date or date range' },
              { step: '03', title: 'Get Analysis', desc: 'NASA-recommended Meteomatics data processed with advanced algorithms' },
              { step: '04', title: 'View Results', desc: 'See probabilities, charts, and insights' }
            ].map((item, index) => (
              <div key={index} className="text-center">
                <div className="w-16 h-16 bg-gradient-to-r from-primary-500 to-purple-500 rounded-full flex items-center justify-center mx-auto mb-4 text-white font-bold text-lg">
                  {item.step}
                </div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">{item.title}</h3>
                <p className="text-gray-600">{item.desc}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="px-6 py-20 bg-gradient-to-r from-primary-600 to-purple-600">
        <div className="max-w-4xl mx-auto text-center">
          <h2 className="text-3xl md:text-4xl font-bold text-white mb-6">
            Ready to Predict the Weather?
          </h2>
          <p className="text-xl text-primary-100 mb-8">
            Start exploring weather probabilities with NASA-recommended Meteomatics integration today
          </p>
          <button 
            onClick={handleGetStarted}
            className="bg-white text-primary-600 hover:bg-gray-100 font-semibold py-4 px-8 rounded-lg transition-colors duration-200 shadow-lg hover:shadow-xl inline-flex items-center space-x-2 group"
          >
            <span>Launch Dashboard</span>
            <ArrowRight className="h-5 w-5 group-hover:translate-x-1 transition-transform duration-200" />
          </button>
        </div>
      </section>

      {/* Footer */}
      <footer className="px-6 py-8 bg-gray-900 text-center">
        <p className="text-gray-400">
          © 2024 WeatherPredict. NASA-Recommended Meteomatics Integration.
        </p>
      </footer>
    </div>
  );
};

export default LandingPage;

/**
 * GIBSLayerGrid
 * Fetches WMTS GetCapabilities from GIBSService, parses XML, and displays a responsive grid
 */
function GIBSLayerGrid() {
  const [layers, setLayers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const CACHE_KEY = 'gibs_capabilities_cache_v1';
  const CACHE_TTL_MS = 1000 * 60 * 60 * 24; // 24 hours

  useEffect(() => {
    let mounted = true;

    async function load() {
      try {
        setLoading(true);

        // Try cache first
        const raw = localStorage.getItem(CACHE_KEY);
        if (raw) {
          try {
            const parsedCache = JSON.parse(raw);
            if (parsedCache.timestamp && (Date.now() - parsedCache.timestamp) < CACHE_TTL_MS) {
              if (mounted) {
                setLayers(parsedCache.layers.slice(0, 20));
                setLoading(false);
              }
              return;
            }
          } catch (err) {
            console.warn('Invalid GIBS cache, refetching');
          }
        }

        const xmlText = await GIBSService.getCapabilities();

        // Parse XML using DOMParser (browser environment)
        const parser = new DOMParser();
        const doc = parser.parseFromString(xmlText, 'application/xml');

        const layerNodes = Array.from(doc.querySelectorAll('Layer'));
        const parsed = layerNodes.map(node => {
          const idNode = node.querySelector('Identifier');
          const titleNode = node.querySelector('Title');
          const tileMatrixSetNode = node.querySelector('TileMatrixSetLink > TileMatrixSet');
          return {
            id: idNode ? idNode.textContent.trim() : 'unknown',
            title: titleNode ? titleNode.textContent.trim() : idNode ? idNode.textContent.trim() : 'Untitled',
            tileMatrixSet: tileMatrixSetNode ? tileMatrixSetNode.textContent.trim() : null
          };
        }).filter(Boolean);

        // De-duplicate by id and prefer named title
        const unique = [];
        const seen = new Set();
        for (const p of parsed) {
          if (!seen.has(p.id)) {
            seen.add(p.id);
            unique.push(p);
          }
        }

        // Persist to cache
        try {
          localStorage.setItem(CACHE_KEY, JSON.stringify({ timestamp: Date.now(), layers: unique }));
        } catch (err) {
          console.warn('Failed to write GIBS cache', err.message);
        }

        // Limit to 20 items for the landing page
        if (mounted) setLayers(unique.slice(0, 20));
      } catch (e) {
        console.error('GIBS capabilities parse error', e);
        if (mounted) setError(e.message || String(e));
      } finally {
        if (mounted) setLoading(false);
      }
    }

    load();

    return () => { mounted = false; };
  }, []);

  if (loading) return <div className="text-center py-12">Loading GIBS layers…</div>;
  if (error) return <div className="text-center py-12 text-red-600">Failed to load GIBS layers: {error}</div>;

  const handleRefresh = () => {
    try { localStorage.removeItem(CACHE_KEY); } catch (e) { /* ignore */ }
    setLayers([]);
    setLoading(true);
    setError(null);
    // re-run effect by toggling a simple reload via setTimeout
    setTimeout(() => {
      // naive re-fetch: call load by re-mounting effect through state trick
      window.location.reload();
    }, 100);
  };

  return (
    <div>
      <div className="flex justify-end mb-4">
        <button onClick={handleRefresh} className="btn-secondary">Refresh Layers</button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-4">
        {layers.map((layer) => {
          // Build a small preview tile using GIBSService (low zoom)
          const previewUrl = GIBSService.getTileUrl({ layer: layer.id, tileMatrix: 1, tileRow: 0, tileCol: 0 });
          return (
            <div key={layer.id} className="card p-2 bg-white/70 hover:shadow-lg transition-shadow duration-200">
              <div className="h-32 bg-gray-100 rounded overflow-hidden mb-3 flex items-center justify-center">
                <img src={previewUrl} alt={layer.title} className="object-cover w-full h-full" loading="lazy" onError={(e)=>{e.currentTarget.style.display='none';}} />
              </div>
              <h4 className="font-semibold text-gray-900 truncate">{layer.title}</h4>
              <p className="text-sm text-gray-600 truncate">{layer.id}</p>
              <p className="text-xs text-gray-500 mt-2">TileMatrixSet: {layer.tileMatrixSet || 'n/a'}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
}
