import React, { useState } from 'react';
import { 
  Thermometer,
  CloudRain,
  Wind,
  Fire,
  Droplets,
  Sun,
  CloudSnow,
  Eye
} from 'lucide-react';

const MonitoringPanel = ({ onLayerSelect, selectedLayer }) => {
  const [activeCategory, setActiveCategory] = useState('weather');

  const layerCategories = {
    weather: {
      title: 'Weather Layers',
      icon: CloudRain,
      layers: [
        { id: 'clouds', name: 'Cloud Coverage', icon: Cloud },
        { id: 'precipitation', name: 'Precipitation', icon: CloudRain },
        { id: 'temperature', name: 'Temperature', icon: Thermometer },
        { id: 'wind', name: 'Wind Speed', icon: Wind },
      ]
    },
    monitoring: {
      title: 'Environmental Monitoring',
      icon: Eye,
      layers: [
        { id: 'fires', name: 'Active Fires', icon: Fire },
        { id: 'snow', name: 'Snow Cover', icon: CloudSnow },
        { id: 'waterVapor', name: 'Water Vapor', icon: Droplets },
        { id: 'aerosol', name: 'Aerosol', icon: Sun },
      ]
    }
  };

  return (
    <div className="bg-white rounded-lg shadow p-4">
      <h2 className="text-xl font-semibold mb-4">Environmental Monitoring</h2>
      
      {/* Category Tabs */}
      <div className="flex space-x-2 mb-4">
        {Object.entries(layerCategories).map(([category, { title, icon: Icon }]) => (
          <button
            key={category}
            onClick={() => setActiveCategory(category)}
            className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-colors ${
              activeCategory === category
                ? 'bg-blue-500 text-white'
                : 'bg-gray-100 hover:bg-gray-200'
            }`}
          >
            <Icon className="h-4 w-4" />
            <span>{title}</span>
          </button>
        ))}
      </div>

      {/* Layer Selection Grid */}
      <div className="grid grid-cols-2 gap-3">
        {layerCategories[activeCategory].layers.map(({ id, name, icon: Icon }) => (
          <button
            key={id}
            onClick={() => onLayerSelect(id)}
            className={`flex items-center space-x-2 p-3 rounded-lg border transition-colors ${
              selectedLayer === id
                ? 'border-blue-500 bg-blue-50'
                : 'border-gray-200 hover:border-blue-300'
            }`}
          >
            <Icon className={`h-5 w-5 ${selectedLayer === id ? 'text-blue-500' : 'text-gray-600'}`} />
            <span className="font-medium">{name}</span>
          </button>
        ))}
      </div>

      {/* Layer Information */}
      <div className="mt-4 p-3 bg-gray-50 rounded-lg">
        <p className="text-sm text-gray-600">
          Select a layer to visualize different environmental parameters. Data provided by NASA GIBS.
        </p>
      </div>
    </div>
  );
};

export default MonitoringPanel;
