  const fetchAll = React.useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      // Fetch analysis for each location in parallel (simulated data from Meteomatics service)
      const promises = LOCATIONS.map(loc =>
        weatherService.getWeatherAnalysis(loc.lat, loc.lon, new Date().toISOString())
          .then(result => ({ loc, result }))
          .catch(err => ({ loc, error: err }))
      );

      const results = await Promise.all(promises);

      // Map each response into WeatherGrid shape
      const mapped = results.map(item => {
        if (item.error) {
          return {
            id: item.loc.id,
            location: item.loc.name,
            temp: null,
            feels_like: null,
            condition: 'clouds',
            wind_kmh: null,
            precip_mm: null,
            time: new Date().toISOString(),
            probability: null
          };
        }

        const weather = item.result?.daily?.slice(-1)[0] || {};

        return {
          id: item.loc.id,
          location: item.loc.name,
          temp: weather.temperature != null ? weather.temperature : (weather.temperature || null),
          feels_like: weather.temperature != null ? weather.temperature : null,
          condition: guessConditionFromRecord(weather),
          wind_kmh: weather.windSpeed ?? null,
          precip_mm: weather.precipitation ?? null,
          time: item.result?.metadata?.generatedAt || new Date().toISOString(),
          probability: estimatePrecipProbability(weather)
        };
      });

      setData(mapped);
    } catch (err) {
      console.error('WeatherGridWrapper fetch error', err);
      setError(err.message || String(err));
    } finally {
      setLoading(false);
    }
  }, []);

  React.useEffect(() => {
    fetchAll();

    // wire refresh button (simple DOM hook)
    const btn = document.getElementById('refresh-weather');
    if (btn) {
      const handler = () => fetchAll();
      btn.addEventListener('click', handler);
      return () => btn.removeEventListener('click', handler);
    }
  }, [fetchAll]);

  return <WeatherGrid data={data} loading={loading} error={error} unit="C" />;
}

function guessConditionFromRecord(rec = {}) {
  // Simple heuristic: precipitation > 5 => thunder/rain; >0 => rain; low temp and precip => snow; else clear/clouds
  const p = rec.precipitation ?? 0;
  const t = rec.temperature != null ? rec.temperature : null;

  if (p >= 10) return 'thunder';
  if (p > 0) return 'rain';
  if (t !== null && t <= 0) return 'snow';
  // fallback by random small chance of clouds
  return Math.random() > 0.3 ? 'clear' : 'clouds';
}

function estimatePrecipProbability(rec = {}) {
  if (!rec || rec.precipitation == null) return null;
  // heuristic: map precipitation amount to a 0-100 probability
  const p = Math.min(100, Math.round((rec.precipitation / 20) * 100));
  return p;
}
